zc.loadAds= (divId, sizes, type) => { 
window.googletag = window.googletag || {cmd: []};
  googletag.cmd.push(function() {
    googletag.defineSlot('/1769731/'+type, sizes, divId).addService(googletag.pubads());
    googletag.pubads().enableSingleRequest();
    googletag.enableServices();
  });
googletag.cmd.push(function() { googletag.display(divId); });
  }

zc.shareClip = (type) => { 
const params = new URLSearchParams();
  if (type == 'fb') {params.set('u', window.location.href);const fbShareUrl = 'https://www.facebook.com/sharer/sharer.php?' + params;
window.open(fbShareUrl, '_blank', 'toolbar=yes,scrollbars=yes,resizable=yes,top=200,left=400,width=650,height=450');} else if (type == 'twitter') {params.set('url', window.location.href);const twitterShareUrl = 'https://twitter.com/share?' + params;window.open(twitterShareUrl, '_blank', 'toolbar=yes,scrollbars=yes,resizable=yes,top=200,left=400,width=650,height=450');}
  }
